import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';
import FavoriteCard from '../components/FavoriteCard'; // Import the FavoriteCard component

const FavoritesPage = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Token'dan kullanıcı bilgisini çıkar
    const userEmail = localStorage.getItem('userEmail');
    if (userEmail) {
      // userEmail ile favori ürünleri seçicez
      axios.get(`http://localhost:5000/api/users/favorites/get/${userEmail}`)
      .then(response => {
        setFavorites(response.data);
      })
      .catch(err => {
        console.error('Failed to fetch favorites', err);
      });
    }
  }, []);

  return (
    <div>
      <Header />
      <div className="pt-16" style={{ minHeight: '100vh'}}>
        <div className="bg-gray-800 text-white p-4 shadow">
          <div className="container mx-auto">
            <h2 className="text-2xl font-bold">Your Favorite Products</h2>
          </div>
        </div>
        <div className="container mx-auto mt-10">
          <div className="grid grid-cols-3 gap-4">
            {favorites.map((item) => (
              <FavoriteCard key={item._id} product={item} />
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default FavoritesPage;
