import React from 'react';

const SimilarCard = ({ product }) => {
  return (
    <div className="bg-white p-4 rounded-lg shadow-md flex flex-row items-center">
      <div className="w-1/8">
        <img
          src={product.productImage}
          alt={product.productName}
          className="rounded-lg shadow-md"
          style={{ width: '100%', height: 'auto', objectFit: 'contain' }}
        />
      </div>
      <div className="w-3/4 pl-4">
        <h3 className="text-lg font-semibold">{product.productName}</h3>
        <p className="text-md text-gray-500">Sold by: {product.productSeller}</p>
        <p className="text-md font-bold text-gray-800">Price: ${product.productPrice}</p>
        <div className="flex items-center space-x-1 mt-2">
          {[...Array(5)].map((_, index) => (
            <svg
              key={index}
              className={`w-4 h-4 rating-star ${
                index < Math.floor(parseFloat(product.productRating)) ? 'text-[#fde047]' : 'text-[#e5e7eb]'
              }`}
              xmlns="http://www.w3.org/2000/svg"
              fill="currentColor"
              viewBox="0 0 22 20"
            >
              <path d="M20.924 7.625a1.523 1.523 0 0 0-1.238-1.044l-5.051-.734-2.259-4.577a1.534 1.534 0 0 0-2.752 0L7.365 5.847l-5.051.734A1.535 1.535 0 0 0 1.463 9.2l3.656 3.563-.863 5.031a1.532 1.532 0 0 0 2.226 1.616L11 17.033l4.518 2.375a1.534 1.534 0 0 0 2.226-1.617l-.863-5.03L20.537 9.2a1.523 1.523 0 0 0 .387-1.575Z"/>
            </svg>
          ))}
        </div>
        <span className="bg-[#dbeafe] text-[#1e40af] text-xs font-semibold px-2.5 py-0.5 rounded dark:bg-[#dbeafe] dark:text-[#1e40af] ms-3 items-center">{product.productRating} in {product.productRatingCount} Rating</span>
      </div>
    </div>
  );
};


export default SimilarCard;
