import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Loader from '../components/Loader';
import Card from '../components/Card';
import { useProductContext } from '../context/ProductContext';
import axios from 'axios';

const ProductPage = () => {
  const { productData } = useProductContext();
  const [similarProducts, setSimilarProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [predictedPrice, setPredictedPrice] = useState(null);

  useEffect(() => {
    if (productData) {
      const query = productData.productName.split(' ').slice(0, 4).join(' ');
      fetchSimilarProducts(query);
      if (!predictedPrice) { // Eğer tahmin edilmiş fiyat yoksa hesapla
        generatePricePrediction(parseFloat(productData.productPrice)); // parseFloat ile fiyatı sayısal değere çevir
      }
    }
  }, [productData]); // productData değiştiğinde bu kod çalışır

  const generatePricePrediction = (currentPrice) => {
    const priceFluctuations = [-5, -2, -3, -1, 3, 2, 1, 5, 4];
    const randomAdjustment = priceFluctuations[Math.floor(Math.random() * priceFluctuations.length)];
    setPredictedPrice(currentPrice + randomAdjustment);
  };

  const addToFavorites = async () => {
    setLoading(true);
    try {
      const userEmail = localStorage.getItem('userEmail');
      const {
        productName,
        productPrice,
        productLink,
        productImage,
        productSeller,
        productRating,
        productRatingCount,
      } = productData;

      const response = await axios.post('http://localhost:5000/api/users/favorites/add', {
        userEmail,
        productName,
        productPrice,
        productLink,
        productImage,
        productSeller,
        productRating,
        productRatingCount,
      });

      if (response.status === 201) {
        alert('Product added to favorites!');
      } else if (response.status === 400) {
        alert('Product already exists in favorites!');
      }
    } catch (error) {
      console.error('Failed to add product to favorites', error);
      alert('Failed to add product to favorites');
    } finally {
      setLoading(false);
    }
  };

  const fetchSimilarProducts = async (query) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/search/similar/${query}`);
      setSimilarProducts(response.data);
    } catch (error) {
      console.error("Error fetching data", error);
    } finally {
      setLoading(false);
    }
  };

  if (!productData) {
    return <div className="container mx-auto mt-44 text-center">No product data found</div>;
  }

  return (
    <div>
      <Header />
      {loading && <Loader />}
      <div className="container mx-auto mt-20 py-10" style={{ minHeight: '100vh' }}>
        <div className="bg-white p-8 rounded-lg shadow-lg flex flex-col md:flex-row items-start md:items-center">
          {/* Product Image */}
          <div className="w-full md:w-1/3 mb-6 md:mb-0">
            <img
              src={productData.productImage}
              alt={productData.productName}
              className="rounded-lg shadow-md"
              style={{ width: '100%', height: 'auto', objectFit: 'contain' }}
            />
          </div>

          {/* Product Details */}
          <div className="w-full md:w-2/3 md:pl-8">
            <h2 className="text-3xl font-semibold mb-3">{productData.productName}</h2>
            <p className="text-lg text-gray-500 mb-1">Sold by: {productData.productSeller}</p>
            <p className="text-md text-gray-700 mb-4">{productData.productDescription}</p>
            <div className="flex flex-wrap items-center justify-start gap-4">
              <span className="text-xl font-bold text-gray-800">Price: ${productData.productPrice}</span>
              {predictedPrice && (
                <span className="text-lg text-red-500">Predicted Price in 15-20 days: ${predictedPrice}</span>
              )}             
              <button
                className="text-white bg-[#1d4ed8] hover:bg-[#1e40af] active:bg-[#1e3a8a] focus:ring-4 focus:outline-none focus:ring-[#93c5fd] font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-[#2563eb] dark:hover:bg-[#1d4ed8] dark:active:[#1e40af] dark:focus:ring-[#1e40af] cursor-pointer"
                onClick={addToFavorites}
              >
                Add to Favorites
              </button>
              <a href={productData.productLink} target="_blank" rel="noopener noreferrer" className="text-white bg-[#0dea34] hover:bg-[#08a824] active:bg-[#08a824] focus:ring-4 focus:outline-none focus:ring-[#12fa3b] font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-[#2563eb] dark:hover:bg-[#1d4ed8] dark:active:[#1e40af] dark:focus:ring-[#1e40af] cursor-pointer">
              View Product
              </a>
            </div>
          </div>
        </div>

        {/* Similar Products */}
        {similarProducts.length > 0 && (
          <div className="mt-10">
            <h2 className="text-2xl font-semibold mb-4">Similar Products</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
              {similarProducts.map((item, index) => (
                <div key={index} className="p-4 shadow rounded-lg bg-white">
                  <img src={item.productImage} alt={item.productName} className="w-full h-64 object-cover rounded" />
                  <div className="mt-4">
                    <h3 className="text-xl font-semibold">{item.productName}</h3>
                    <p className="text-gray-600">{item.productSeller}</p>
                    <p className="text-gray-800">${item.productPrice}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default ProductPage;
