import React, { useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import axios from 'axios';

const ContactPage = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/sendEmail', { email, message });
      alert('Email sent successfully!');
      setEmail('');
      setMessage('');
    } catch (error) {
      console.error('Failed to send email', error);
      alert('Failed to send email');
    }
  };

  return (
    <div>
      <Header />
      <div className="container mx-auto mt-20 py-10" style={{ minHeight: '100vh' }}>
        <div className="max-w-lg mx-auto">
          <h1 className="text-4xl font-bold text-center mb-6">Contact Us</h1>
          <form onSubmit={handleSubmit}>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">Your Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              required
            />
            <label htmlFor="message" className="block text-sm font-medium text-gray-700 mt-4">Your Message</label>
            <textarea
              id="message"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              rows="4"
              className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              required
            />
            <button type="submit" className="mt-6 px-6 py-2 bg-blue-500 text-white rounded hover:bg-blue-700 transition duration-300">
              Send Email
            </button>
          </form>
          <div className="mt-10 text-center">
            <h2 className="text-2xl font-bold">Get in Touch</h2>
            <p className="mt-2 text-lg text-gray-700">Email: your-email@example.com</p>
            <p className="text-lg text-gray-700">Phone: +1 234 567 890</p>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ContactPage;
