import React, { useState, useEffect } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Card from '../components/Card';
import axios from 'axios';
import { useSearchContext } from '../context/SearchContext';

const predefinedProducts = [
  'iphone 12', 'Samsung Galaxy S10', 'MacBook Pro', 'PlayStation 5', 'Xbox Series X', 'Nintendo Switch', 'Dell XPS 13', 'iPad Air', 'Sony WH-1000XM4', 'GoPro Hero 9'
];

function HomePage() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const { searchResults, setSearchResults } = useSearchContext();
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setIsLoggedIn(true);
      navigate('/');
    } else {
      setIsLoggedIn(false);
    }
  }, []);

  useEffect(() => {
    const fetchRandomProducts = async () => {
      const randomProduct = predefinedProducts[Math.floor(Math.random() * predefinedProducts.length)];
      setSearchQuery(randomProduct);
      await handleSearch(randomProduct);
    };

    fetchRandomProducts();
  }, []);

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/search/${query}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  if (!isLoggedIn) {
    return <Navigate to="/login" />;
  }

  return (
    <div>
      <Header handleSearch={handleSearch} />

      <div className="container mx-auto mt-44 flex items-center justify-center">
        <div className="w-full md:w-2/3 shadow-lg">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 my-12">
            {searchResults.map((product, index) => (
              <div key={index} className="flex">
                <Card product={product} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
